<?php		$tableau=array(); // Création de mon tableau 	$fichier=fopen('orientation.csv', 'r'); // Ouverture de mon fichier 			while ($ligne=fgetcsv($fichier,1024,";")) { // Boucle de remplisage tableau principal			array_push($tableau, $ligne);		}			fclose($fichier); 			function Pearson($xtab,$ytab){ // Création de la fonction de corrélation de Pearson			if(count($xtab) !==count($ytab))// Comparaison de la taille des 2 matrices				return -1;			$x =  array_values($xtab);			$y = array_values($ytab);			$xs = array_sum($x) / count($x);			$ys = array_sum($y) / count($y);			$a = 0; $bx = 0; $by = 0;			for($i = 0; $i < count($x); $i++){				$xr = $x[$i] - $xs;				$yr = $y[$i] - $ys;				$a+=$xr*$yr;				$bx+=pow($xr, 2);				$by+=pow($yr, 2);			}				$b = sqrt($bx*$by);			return $a/$b;		}			$k=0;	$reponses=array_values($tableau); // Index des lignes dans le tableau des réponses	$correlation=array();				while ($k < count($reponses)-1)  // Boucle de remplissage tableau enfant 1			{				$provisoire=array();				for($i = 0; $i < count($reponses)-1; $i++) 				{						$xtab = $tableau[$k]; 						if($k==0)							$ytab = $tableau[$i+1];						else							$ytab = $tableau[$i];					array_push($provisoire, (Pearson($xtab,$ytab)));				}				array_push($correlation, $provisoire);				$k+=1;			}				$copiecorrelation = array();	$copiecorrelation = $correlation; // Affichage du résultat 	$k=0;	$maxcorr=array();				while ($k < count($reponses)-1) {       // Boucle de remplissage tableau enfant 2 				for($i = 0; $i < count($reponses)-1; $i++) 				{					if($copiecorrelation[$k][$i] == 1) // Exclure la comparasion entre une même personne					{						$copiecorrelation[$k][$i] -=2;					}				}				$maxcorr[$k]=max($copiecorrelation[$k]); // Correlation la plus forte				$indice[$k]=array_keys($copiecorrelation[$k],$maxcorr[$k]); // Id de la personne la plus ressemblante				$k+=1;			}				$tableau=array();	$fichier=fopen('identifiant.csv', 'r');		// Lecture du fichier 				while ($ligne=fgetcsv($fichier,1024,";")) {				array_push($tableau, $ligne);			}			fclose($fichier);				echo "<br><B><u>Voici une recommandation de métier et de filière qui vous irait bien &#128521; </u></B> ";	echo "<br>";				for($i = 0; $i < count($reponses)-1; $i++)			{				$k=0;				while($k < count($reponses)-1 ){					if($indice[$i][0]==$k+1){								echo "<br>";								echo "<B>" .$tableau[$i][1]  .'</B> nous vous recommandons le domaine de <B>' .$tableau[$k+2][0] .'</B> en obtenant un bac <B> '.$tableau[$k+2][3].'</B>' ;					echo "<br>";				}				$k++;				}			}				print_r(tableau[$k+2][0]);?>